  return (
    <div className='login-popup'>
      <form onSubmit={onLogin} className='login-popup-container'>
        <div className='login-popup-title'>
            <h2>{currState}</h2>
            <img onClick={()=> setShowLogin(false)} src={assets.cross_icon}></img>
        </div>
        <div className='login-popup-inputs'>
         {currState==="Login"? <></>: <input name='name' onChange={onChangeHandler} value={data.name} type='text' placeholder='Your name' required />}
          {currState==="Login"?<></>: <input name='phoneNumber' onChange={onChangeHandler} value={data.phoneNumber} type='text' placeholder='Phone Number' required />}

          <input name='email' onChange={onChangeHandler} value={data.email} type='email' placeholder='Your email' required />
          <input name='password' onChange={onChangeHandler} value={data.password} type='password' placeholder='Password' required />
          {currState==="Login"? <></>: <input name='passwordConfirm' onChange={onChangeHandler} value={data.passwordConfirm} type='password' placeholder='confirm Password' required />}
        </div>
        <button type='submit' >{currState==="Sign Up"?"Create account":"Login"}</button>
        <div className='login-popup-condition'>
        <input type='checkbox' required/>
        <p>By continuing, I agree to the terms of use & privacy policy</p>
        </div>
        {currState === "Login" ? <p>Create a new account? <span onClick={()=> setCurrState("Sign Up")} >Click here</span></p>
         : <p>Already have an account? <span onClick={() => setCurrState("Login")}>Login here</span></p> }



      </form>
    </div>
  )
