import React, { useContext, useState } from 'react';
import './LoginPopUp.css';
import { assets } from '../../assets/assets';
import { StoreContext } from '../../context/StoreContext';
import axios from 'axios';
import { Link } from 'react-router-dom';

const LoginPopUp = ({ setShowLogin }) => {
  const { url, setToken } = useContext(StoreContext);
  const [error, setError] = useState();

  const [currState, setCurrState] = useState('Login');
  const [data, setData] = useState({
    name: "",
    email: "",
    phoneNumber: "",
    password: "",
    passwordConfirm: ""
  });


  const onChangeHandler = (event) => {
    const { name, value } = event.target;
    setData(prevData => ({ ...prevData, [name]: value }));
  };

  const onLogin = async (event) => {
    event.preventDefault();

    if (currState !== "Login" && data.password !== data.passwordConfirm) {
      setError("Passwords does not match!");
      return;
    }

    let endpoint = currState === "Login" ? "/api/user/login" : "/api/user/register";
    let apiUrl = `${url}${endpoint}`;

    try {
      const response = await axios.post(apiUrl, data);

      if (response.data.success) {
        setToken(response.data.token);
        localStorage.setItem("token", response.data.token);
        setShowLogin(false);
      } else {
        setError(response.data.message || "An error occurred. Please try again.");
      }
    } catch (error) {
      console.error("Login/Register Error:", error);
      setError(error.response?.data?.message || "An error occurred. Please try again.");
    }
  };

  return (
    <div className='login-popup'>
      <form onSubmit={onLogin} className='login-popup-container'>
        <div className='login-popup-title'>
          <h2>{currState}</h2>
          <img onClick={() => setShowLogin(false)} src={assets.cross_icon} alt="Close"></img>
        </div>
        <div className='login-popup-inputs'>
          {currState === "Login" ? null : (
            <>
              <input name='name' onChange={onChangeHandler} value={data.name} type='text' placeholder='Your name' required />
              <input name='phoneNumber' onChange={onChangeHandler} value={data.phoneNumber} type='text' placeholder='Phone Number' required />
            </>
          )}
          <input name='email' onChange={onChangeHandler} value={data.email} type='email' placeholder='Your email' required />
          <input name='password' onChange={onChangeHandler} value={data.password} type='password' placeholder='Password' required />
          {currState === "Login" ? null : (
            <input name='passwordConfirm' onChange={onChangeHandler} value={data.passwordConfirm} type='password' placeholder='Confirm Password' required />
          )}
        </div>
        <button type='submit'>{currState === "Sign Up" ? "Create account" : "Login"}</button>
        {currState === "Login" ? (
          <Link to="/forgotpassword"><p>Forgot Password</p></Link>
        ) : (
          <div className='login-popup-condition'>
            <input type='checkbox' required />
            <p>By continuing, I agree to the terms of use & privacy policy</p>
          </div>
        )}
        {currState === "Login" ? (
          <p>Create a new account? <span onClick={() => setCurrState("Sign Up")}>Click here</span></p>
        ) : (
          <p>Already have an account? <span onClick={() => setCurrState("Login")}>Login here</span></p>
        )}
        <div>
      {error && <p className="error-message">{error}</p>}
    </div>
      </form>
    </div>
  );
};

export default LoginPopUp;
