import React from 'react';
import './ExploreMenu.css';
import { menu_list } from '../../assets/assets';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';




const ExploreMenu = ({ category, setCategory }) => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 1000,
    slidesToShow: 3, // Number of items to show at a time
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
    centerMode: true, // Center the current slide
    centerPadding: '0', // Remove padding around center slide
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
        },
      },
    ],
  };

  return (
    <div className="explore-menu" id="explore-menu">
      <h1>Explore our Products</h1>
      <p className="explore-menu-text">
        Browse our collections and find the perfect gadget for you. Whether you’re looking to enhance your home or stay on top of the latest tech trends, we got you covered.
      </p>
      <div className="explore-menu-list">
        <Slider {...settings}>
          {menu_list.map((item, index) => (
            <div
              onClick={() => setCategory((prev) => (prev === item.menu_name ? 'All' : item.menu_name))}
              key={index}
              className="explore-menu-list-item"
            >
              <img className={category === item.menu_name ? 'active' : ''} src={item.menu_image} alt={item.menu_name} />
              <p>{item.menu_name}</p>
            </div>
          ))}
        </Slider>
      </div>
      <hr />
    </div>
  );
};

export default ExploreMenu;
