import React from 'react';
import './ExploreMenu.css';
import { menu_list } from '../../assets/assets';
import { motion } from 'framer-motion';  // Import Framer Motion

const ExploreMenu = ({ category, setCategory }) => {

  const containerVariants = {
    hidden: { opacity: 0, x: '-100vw' },  // Start from left outside the viewport
    visible: { 
      opacity: 1, 
      x: 0,  // Slide into place
      transition: {
        type: 'spring', 
        stiffness: 50, 
        delay: 0.2
      }
    }
  };

  return (
    <motion.div
      className='explore-menu'
      id='explore-menu'
      variants={containerVariants}
      initial="hidden"
      animate="visible"
    >
      <h1>Explore our Products</h1>
      <p className='explore-menu-text'>
        Browse our collections and find the perfect gadget for you. Whether you’re looking to enhance your home or stay on top of the latest tech trends, we got you covered.
      </p>
      <div className='explore-menu-list'>
        {menu_list.map((item, index) => (
          <motion.div
            key={index}
            className='explore-menu-list-item'
            onClick={() => setCategory(prev => prev === item.menu_name ? "All" : item.menu_name)}
            whileHover={{ scale: 1.05 }}  // Adds a subtle hover effect
            whileTap={{ scale: 0.95 }}    // Adds a click effect
          >
            <img className={category === item.menu_name ? "active" : ""} src={item.menu_image} />
            <p>{item.menu_name}</p>
          </motion.div>
        ))}
      </div>
      <hr />
    </motion.div>
  );
};

export default ExploreMenu;
