import React, { useState } from 'react';
import axios from 'axios';
import './ForgotPassword.css'; // Ensure this path is correct
import { useParams, useNavigate } from 'react-router-dom';
import { assets } from '../../assets/assets';
import { Link } from 'react-router-dom';

const ResetPassword = ({ setShowLogin }) => {
  const { id, token } = useParams();
  const navigate = useNavigate();
  const [password, setPassword] = useState('');
  const [passwordConfirm, setPasswordConfirm] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Check if passwords match before submitting
    if (password !== passwordConfirm) {
      setMessage('Passwords do not match!');
      return;
    }

    try {
      const response = await axios.post(`http://localhost:4000/api/user/resetpassword/${id}/${token}`, { password });

      if (response.data.success) {
        setMessage('Password has been reset. Redirecting to the home page...');
        setShowLogin(false);

        // Delay the redirect by 3 seconds so the user can see the message
        setTimeout(() => {
          navigate('/');
        }, 3000);
      } else {
        setMessage(response.data.message);
      }
    } catch (error) {
      console.error(error);
      setMessage('An error occurred. Please try again.');
    }
  };

  return (
    <div className='forgot-popup'>
      <form onSubmit={handleSubmit} className='forgot-popup-container'>
        <div className='forgot-popup-title'>
          <h2>Reset Your Password</h2>
          <Link to='/'>
            <img src={assets.cross_icon} alt='Close' />
          </Link>
        </div>
        <div className='forgot-popup-inputs'>
          <input
            type='password'
            placeholder='New Password'
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <input
            type='password'
            placeholder='Confirm New Password'
            value={passwordConfirm}
            onChange={(e) => setPasswordConfirm(e.target.value)}
            required
          />
        </div>
        <button type='submit'>Reset Password</button>
        {message && <p className='message'>{message}</p>}
      </form>
    </div>
  );
};

export default ResetPassword;
