import React from 'react';
import './ExploreMenu.css';
import { menu_list } from '../../assets/assets';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const ExploreMenu = () => {
  const settings = {
    dots: true,                    // No dots for navigation
    infinite: true,                 // Infinite scrolling
    speed: 500,                     // Transition speed
    slidesToShow: 7,                // Number of slides to show
    slidesToScroll: 1,              // Number of slides to scroll at a time
    autoplay: true,                 // Enable autoplay
    autoplaySpeed: 2000,            // Autoplay speed (2 seconds)
    cssEase: "linear",              // Smooth scrolling
    pauseOnHover: true,             // Pause when hovered
    responsive: [                   // Make it responsive
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
        }
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 2,
        }
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
        }
      }
    ]
  };

  return (
    <div className='explore-menu' id='explore-menu'>
      <h1>Explore our Products</h1>
      <p className='explore-menu-text'>
        Browse our collections and find the perfect gadget for you. Whether you’re looking to enhance your home or stay on top of the latest tech trends, we got you covered.
      </p>

      <Slider {...settings} className="explore-menu-list">
        {menu_list.map((item, index) => (
          <div key={index} className="explore-menu-list-item">
            <img src={item.menu_image} alt={item.menu_name} />
            <p>{item.menu_name}</p>
          </div>
        ))}
      </Slider>
      <hr />
    </div>
  );
};

export default ExploreMenu;
