import React, { useRef } from 'react';
import './ExploreMenu.css';
import { menu_list } from '../../assets/assets';

const ExploreMenu = ({ category, setCategory }) => {
  const sliderRef = useRef(null);
  let isDown = false;
  let startX;
  let scrollLeft;

  const handleMouseDown = (e) => {
    const slider = sliderRef.current;
    isDown = true;
    slider.classList.add('active');
    startX = e.pageX - slider.offsetLeft;
    scrollLeft = slider.scrollLeft;
  };

  const handleMouseLeave = () => {
    isDown = false;
    sliderRef.current.classList.remove('active');
  };

  const handleMouseUp = () => {
    isDown = false;
    sliderRef.current.classList.remove('active');
  };

  const handleMouseMove = (e) => {
    if (!isDown) return;
    e.preventDefault();
    const slider = sliderRef.current;
    const x = e.pageX - slider.offsetLeft;
    const walk = (x - startX) * 3; // Adjust the scroll speed when dragging
    slider.scrollLeft = scrollLeft - walk;
  };

  return (
    <div className="explore-menu" id="explore-menu">
      <h1>Explore our Products</h1>
      <p className="explore-menu-text">
        Browse our collections and find the perfect gadget for you. Whether you're looking to enhance your home or stay on top of the latest tech trends, we got you covered.
      </p>

      <div
        className="explore-menu-list"
        ref={sliderRef}
        onMouseDown={handleMouseDown}
        onMouseLeave={handleMouseLeave}
        onMouseUp={handleMouseUp}
        onMouseMove={handleMouseMove}
      >
        <div className="explore-menu-list-wrapper">
          {menu_list.map((item, index) => (
            <div
              key={index}
              className="explore-menu-list-item"
              onClick={() => setCategory(prev => (prev === item.menu_name ? "All" : item.menu_name))}
            >
              <img src={item.menu_image} alt={item.menu_name} />
              <p>{item.menu_name}</p>
            </div>
          ))}
          {/* Duplicate the list to ensure infinite scroll */}
          {menu_list.map((item, index) => (
            <div
              key={index + menu_list.length}
              className="explore-menu-list-item"
              onClick={() => setCategory(prev => (prev === item.menu_name ? "All" : item.menu_name))}
            >
              <img src={item.menu_image} alt={item.menu_name} />
              <p>{item.menu_name}</p>
            </div>
          ))}
        </div>
      </div>
      <hr />
    </div>
  );
};

export default ExploreMenu;
