import React from 'react';
import './ExploreMenu.css';
import { menu_list } from '../../assets/assets';
import { motion } from 'framer-motion';

const ExploreMenu = () => {
  return (
    <div className='explore-menu' id='explore-menu'>
      <h1>Explore our Products</h1>
      <p className='explore-menu-text'>Browse our collections and find the perfect gadget for you. Whether you’re looking to enhance your home or stay on top of the latest tech trends, we got you covered.</p>

      <motion.div
        className='explore-menu-list'
        animate={{ x: ['0%', '-100%'] }}  // Move from right to left
        transition={{
          repeat: Infinity,               // Repeat infinitely
          duration: 10,                   // Takes 10 seconds to scroll
          ease: 'linear'                  // Smooth continuous motion
        }}
        style={{ display: 'flex', gap: '30px' }}  // Horizontal layout
      >
        {menu_list.map((item, index) => (
          <div key={index} className='explore-menu-list-item'>
            <img src={item.menu_image} alt={item.menu_name} />
            <p>{item.menu_name}</p>
          </div>
        ))}
        {/* Duplicate the items to create a looping effect */}
        {menu_list.map((item, index) => (
          <div key={`duplicate-${index}`} className='explore-menu-list-item'>
            <img src={item.menu_image} alt={item.menu_name} />
            <p>{item.menu_name}</p>
          </div>
        ))}
      </motion.div>
      <hr />
    </div>
  );
};

export default ExploreMenu;
