import React from 'react';
import './ExploreMenu.css';
import { menu_list } from '../../assets/assets';
import { motion } from 'framer-motion';

const ExploreMenu = ({ category, setCategory }) => {

  return (
    <div className='explore-menu' id='explore-menu'>
      <h1>Explore our Products</h1>
      <p className='explore-menu-text'>Browse our collections and find the perfect gadget for you. Whether you’re looking to enhance your home or stay on top of the latest tech trends, we got you covered.</p>

      <div className='explore-menu-list'>
        {menu_list.map((item, index) => (
          <motion.div
            key={index}
            className='explore-menu-list-item'
            onClick={() => setCategory(prev => prev === item.menu_name ? "All" : item.menu_name)}
            initial={{ x: '-100vw' }}  // Start off-screen to the left
            animate={{ x: 0 }}         // Animate to original position
            transition={{ type: 'spring', stiffness: 50, duration: 0.5, delay: index * 0.2 }}  // Sequential animation
            whileHover={{ scale: 1.05 }}  // Slight hover effect
          >
            <img className={category === item.menu_name ? "active" : ""} src={item.menu_image} alt={item.menu_name}/>
            <p>{item.menu_name}</p>
          </motion.div>
        ))}
      </div>
      <hr />
    </div>
  );
};

export default ExploreMenu;
