import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/swiper.min.css'; // Import Swiper styles
import { Navigation, Pagination, Scrollbar, A11y } from 'swiper';
import 'swiper/swiper-bundle.min.css'; // Import Swiper bundle CSS

const ExploreMenu = ({ category, setCategory, menu_list }) => {
  return (
    <div className='explore-menu' id='explore-menu'>
      <h1>Explore our Products</h1>
      <p className='explore-menu-text'>
        Browse our collections and find the perfect gadget for you. Whether you’re looking to enhance your home or stay on top of the latest tech trends, we got you covered.
      </p>
      <Swiper
        modules={[Navigation, Pagination, Scrollbar, A11y]}
        spaceBetween={20} // Space between slides
        slidesPerView={'auto'} // Adjust this for the number of slides visible at a time
        navigation // Optional: adds navigation arrows
        pagination={{ clickable: true }} // Optional: adds pagination bullets
        scrollbar={{ draggable: true }} // Optional: adds a scrollbar
      >
        {menu_list.map((item, index) => (
          <SwiperSlide key={index}>
            <div onClick={() => setCategory(prev => prev === item.menu_name ? "All" : item.menu_name)} className='explore-menu-list-item'>
              <img className={category === item.menu_name ? "active" : ""} src={item.menu_image} alt={item.menu_name} />
              <p>{item.menu_name}</p>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
      <hr />
    </div>
  );
};

export default ExploreMenu;
