import React, { useContext, useState} from 'react'
import './FoodItem.css'
import { assets } from '../../assets/assets'
import { StoreContext } from '../../context/StoreContext'

const FoodItem = ({id,name,price,description,image}) => {

const [items, setCartItems] = useState
//  const {cartItems, addToCart,removeFromCart} = useContext(StoreContext);

  return (
    <div className='food-item'>
      <div className='food-item-img-container'>
      <img className='food-item-image' src={image}/>
      {!cartItems
           ? <img className='add' onClick={()=> addToCart()} src={assets.add_icon_white} />
           : <div className='food-item-counter'>
            <img onClick={()=>removeFromCart()}  src={assets.remove_icon_red}></img>
            <p>{cartItems}</p>
            <img  onClick={()=>addToCart()} src={assets.add_icon_green}></img>
            </div>
      }     
      </div>
      <div className='food-item-info'>
      <div className='food-item-name-rating'>
      <p>{name}</p>
      <img className='rating-stars' src={assets.rating_stars}/>
      </div>
      <p className='food-item-desc'>{description}</p>
      <p className='food-item-price'>${price}</p>
      </div>
    </div>
  )
}

export default FoodItem



