const LoginPopUp = ({setShowLogin}) => {

    const {url, setToken} = useContext(StoreContext)

  const [currState, setCurrState] = useState('Login')
  const [data, setData] = useState ({
    name:"",
    email:"",
    phoneNumber:"",
    password:"",
    passwordConfirm:""
  })

  const onChangeHandler = (event) => {
    const name = event.target.name
    const value = event.target.value

    setData(data=>({...data,[name]:value}))
  }

  // const onLogin = async (event) => {
  //    event.preventDefault()
  //    let newUrl = url;
  //    if(currState === "Login") {
  //     newUrl += "/api/user/login"
  //    }
  //    else {
  //     newUrl += "/api/user/register"
  //    }

  //    const response = await axios.post(newUrl, data);

  //    if (response.data.success) {
  //        setToken(response.data.token)
  //        localStorage.setItem("token", response.data.token);
  //        setShowLogin(false)
  //    }  else {
  //         alert("There is an error")
  //    }


  // }




  const onl = async (event) => {
    event.preventDefault();

    let newUrl = url;
    if (currState === "Login") {
        newUrl += "/api/user/login";
    } else {
        newUrl += "/api/user/register";
    }

    try {
        const response = await axios.post(newUrl, data);

        if (response.data.success) {
            // Handle successful login or registration
            setToken(response.data.token);
            localStorage.setItem("token", response.data.token);
            setShowLogin(false); // Hide login/register form
        } else {
            // Handle server-side errors
            alert(response.data.message || "An error occurred. Please try again.");
        }
    } catch (error) {
        // Handle client-side errors (e.g., network issues)
        const errorMessage = error.response?.data?.message || "An error occurred. Please try again.";
        alert(errorMessage);
    }
  };

  useEffect (()=> {
     console.log(data)
  },[data])

    return (
      <div className='login-popup'>
        <form  className='login-popup-container'>
          <div className='login-popup-title'>
              <h2>{currState}</h2>
              <img onClick={()=> setShowLogin(false)} src={assets.cross_icon}></img>
          </div>
          <div className='login-popup-inputs'>
           {currState==="Login"? <></>: <input name='name' onChange={onChangeHandler} value={data.name} type='text' placeholder='Your name' required />}
            {currState==="Login"?<></>: <input name='phoneNumber' onChange={onChangeHandler} value={data.phoneNumber} type='text' placeholder='Phone Number' required />}

            <input name='email' onChange={onChangeHandler} value={data.email} type='email' placeholder='Your email' required />
            <input name='password' onChange={onChangeHandler} value={data.password} type='password' placeholder='Password' required />
            {currState==="Login"? <></>: <input name='passwordConfirm' onChange={onChangeHandler} value={data.passwordConfirm} type='password' placeholder='confirm Password' required />}
          </div>
          <button type='submit' >{currState==="Sign Up"?"Create account":"Login"}</button>
           {currState==="Login"? <Link to="/forgotpassword" ><p>Forgot Password</p></Link>   : <div className='login-popup-condition'>
          <input type='checkbox' required/>
          <p>By continuing, I agree to the terms of use & privacy policy</p>

          </div> }
          {/* <p>Forgot Password</p> */}
          {currState === "Login" ? <p>Create a new account? <span onClick={()=> setCurrState("Sign Up")} >Click here</span></p>
           : <p>Already have an account? <span onClick={() => setCurrState("Login")}>Login here</span></p> }



        </form>
      </div>
    )
  }

  export default ForgotPassword
